resources.string:
#version 300 es
in vec4 position;

uniform mat4 projection;
uniform mat4 view;
uniform mat4 model;

void main{
vec4 pos = position;
pos.w = 1.0;
    gl_Position = projection * view * model * pos;
}

#version 300 es
        precision mediump float;
        precision mediump int;
        out ivec3 fragColor;
        uniform int objectID;
        void main{
            fragColor = ivec3(objectID);
        }
